import React, { createContext, useEffect, useState } from 'react';
import axios from 'axios';

export const NewsContext = createContext()

export const NewsContextProvider = (props) => {
    const [data, setData] = useState([])
    const apiKey = "87b7a05270c54720a343ed0e7943dcd1";

    useEffect(() => {
        axios.get(`https://newsapi.org/v2/everything?q=tesla&from=2022-08-23&sortBy=publishedAt&apiKey=`).then(response => setData(response.data)).catch(error => console.log(error));
    }, []);

    return (
        <NewsContext.Provider value={{ data }}>
            {props.children}
        </NewsContext.Provider>
    );
};
